INSERT INTO `ezcontentobject_attribute` VALUES (0,351,177,0,NULL,'<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<ezimage serial_number=\"1\" is_valid=\"1\" filename=\"7-keystone-habits-to-maximize-your-mornings.jpg\" suffix=\"jpg\" basename=\"7-keystone-habits-to-maximize-your-mornings\" dirpath=\"var/site/storage/images/fitness/7-keystone-habits-to-maximize-your-mornings/2161-9-eng-GB\" url=\"var/site/storage/images/fitness/7-keystone-habits-to-maximize-your-mornings/2161-9-eng-GB/7-keystone-habits-to-maximize-your-mornings.jpg\" original_filename=\"charlotte-karlsen-768242-unsplash.jpg\" mime_type=\"image/jpeg\" width=\"4032\" height=\"3024\" alternative_text=\"\" alias_key=\"1293033771\" timestamp=\"1535009644\"><original attribute_id=\"2161\" attribute_version=\"9\" attribute_language=\"eng-GB\"/><information Height=\"3024\" Width=\"4032\" IsColor=\"1\"/></ezimage>\n','ezimage',2161,'eng-GB',2,0,'',10);
