INSERT INTO `ezcontentobject_attribute` VALUES (0,771,225,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>This authentic pad thai recipe (made gluten-free) comes from the Chiang Mai Cookery School located in the Northern mountainous part of Thailand.</para></section>\n','ezrichtext',2772,'eng-GB',2,0,'',12);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,225,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',2773,'eng-GB',2,0,'',12);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,225,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para><literallayout class=\"normal\">Serves 4-6\n<emphasis>Adapted from the Chiang Mai Cookery School in Thailand</emphasis></literallayout></para><title ezxhtml:level=\"3\">INGREDIENTS</title><para>For the sauce:</para><itemizedlist><listitem><para>4 tablespoons sugar</para></listitem><listitem><para>6 tablespoons fish sauce (make sure it\'s gluten-free)</para></listitem><listitem><para>2 tablespoons tamari</para></listitem><listitem><para>4 tablespoons tamarind juice (or 2 tablespoons lime juice and 2 tablespoons light brown sugar)</para></listitem></itemizedlist><para>For the pad thai:</para><itemizedlist><listitem><para>3 tablespoons vegetable oil</para></listitem><listitem><para>1 tablespoon garlic, minced</para></listitem><listitem><para>1-2 carrots, julienned</para></listitem><listitem><para>Thai chile peppers, such as Bird\'s eye, thinly sliced</para></listitem><listitem><para>1 cup tofu, cut into cubes (I like to use fried or baked tofu, but regular firm tofu is also good)</para></listitem><listitem><para>1 to 1.5 pounds shrimp</para></listitem><listitem><para>14 ounces dried rice noodles, soaked in water for 10-15 minutes</para></listitem><listitem><para>6 tablespoons chicken stock or water</para></listitem><listitem><para>2 eggs, beaten</para></listitem><listitem><para>5 tablespoons roasted peanuts, chopped and divided</para></listitem><listitem><para>1/4 cup green onions, cut into 1-inch pieces</para></listitem><listitem><para>1-2 cups bean sprouts</para></listitem><listitem><para>Optional, fresh spinach</para></listitem><listitem><para>Fresh cilantro, chopped, for garnish</para></listitem><listitem><para>Lime wedges, for garnish</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Mix sauce ingredients in a small bowl and set aside. (Note: you may have extra sauce left over depending on how \"saucy\" you like your noodles.)</para></listitem><listitem><para>Heat oil in a wok over medium-high heat and fry the garlic, carrots, chili peppers, and tofu until the garlic turns golden brown. Add the shrimp and continue to stir-fry until the shrimp begin to turn pink (take care to not overcook the shrimp). Transfer to a plate and set aside.</para></listitem><listitem><para>Add the softened rice noodles and chicken stock and stir-fry over high heat. Continue stirring until the noodles are softened.</para></listitem><listitem><para>Reduce heat to medium and add half of the sauce; stir until well combined.</para></listitem><listitem><para>Move noodles to one side of the wok and add the beaten eggs; scramble until thoroughly cooked then stir into the noodles. Add more sauce to adjust taste if necessary.</para></listitem><listitem><para>Add 3 tablespoons of the peanuts, the green onions, and bean sprouts; stir-fry until combined.</para></listitem><listitem><para>Transfer to a serving dish and garnish with the remaining chopped peanuts, fresh cilantro, and lime wedges.</para></listitem></orderedlist><para><emphasis>Recipe credit: <link xlink:href=\"ezurl://17\" xlink:show=\"none\">Sheri Wetherell</link> via <link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link> / <link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis>Photo credit: <link xlink:href=\"ezurl://20\" xlink:show=\"none\">sharonang</link> via <link xlink:href=\"ezurl://21\" xlink:show=\"none\">Pixabay</link> / <link xlink:href=\"ezurl://22\" xlink:show=\"none\">CC BY</link></emphasis></para></section>\n','ezrichtext',2774,'eng-GB',2,0,'',12);
