INSERT INTO `ezcontentobject_attribute` VALUES (0,773,227,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><para>For the granola:</para><itemizedlist><listitem><para>1 cup almonds (crushed)</para></listitem><listitem><para>2 cups jumbo rolled oats</para></listitem><listitem><para>1 dash sea salt</para></listitem><listitem><para>1 cup walnut (crushed)</para></listitem><listitem><para>For the almond milk</para></listitem><listitem><para>5 cups almond milk</para></listitem></itemizedlist><para>For the liquids:</para><itemizedlist><listitem><para>2 tablespoons extra virgin olive oil</para></listitem><listitem><para>2 tbsps maple syrup</para></listitem><listitem><para>12 tsp vanilla powder</para></listitem></itemizedlist><para>For the berries:</para><itemizedlist><listitem><para>12 cup dried cranberries (dried and cut in half)</para></listitem><listitem><para>2 tbsps dried goji berries</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Preheat the oven to 300 F (150 degrees).</para></listitem><listitem><para>Mix the oats with the nuts in a big bowl.</para></listitem><listitem><para>Mix the oil with the vanilla, salt and maple syrup in a small separate cup.</para></listitem><listitem><para>Add the liquid oil mixture to the oats and mix it with your hands until it\'s evenly spread.</para></listitem><listitem><para>Put baking paper on the baking sheet before you scatter the granola on it.</para></listitem><listitem><para>Toast the granola for about 10 minutes in the oven and add the berries after that.</para></listitem><listitem><para>It needs to completely cool down before you can fill it into a storage container.</para></listitem><listitem><para>Serve with fortified almond milk.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://23\" xlink:show=\"none\">Matt</link>via<link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis><emphasis>Photo credit:<emphasis><link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></emphasis></emphasis></para></section>\n','ezrichtext',2814,'eng-GB',2,0,'',4);
