INSERT INTO `ezcontentobject_attribute` VALUES (0,771,228,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>This easy recipe only takes about 10 minutes to make  start to finish  and it\'s both delicious and nutritious.</para></section>\n','ezrichtext',2832,'eng-GB',2,0,'',4);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,228,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',2833,'eng-GB',2,0,'',4);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,228,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para><emphasis>Serves 4</emphasis></para><para>Note: if you are not following a low FODMAP diet, feel free to add onions and garlic to this recipe for added flavor.</para><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>1 package ground turkey (about 1+ lb)</para></listitem><listitem><para>Olive oil</para></listitem><listitem><para>1 14-ounce can organic fire roasted tomatoes</para></listitem><listitem><para>Salt and pepper, to taste</para></listitem><listitem><para>Seasoning of your choice, such as a dried Italian herb mix, to taste</para></listitem><listitem><para>4 medium zucchini, spiralized</para></listitem><listitem><para>For garnish, freshly grated Parmesan cheese and a drizzle of *basil olive oil (*see note below)</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Add a drizzle or two of olive oil to a large and heat on medium-high (other oils that are wonderful drizzled over to finish this dish are truffle oil, lemon oil, or any non-flavored high-quality olive oil). Add ground turkey and cook, breaking up the meat with a spatula, until browned, about 3 to 4 minutes. Season to taste with salt, pepper, and Italian herbs. Transfer to a serving bowl and set aside.</para></listitem><listitem><para>Add a bit more olive oil to the skillet (if necessary) and toss in the zucchini zoodles. Cook, tossing frequently, until softened, about 3-5 minutes depending on how cooked you\'d like them (I like them still a bit tender, which is a cook time of about 3 minutes). Transfer to the serving bowl and gently toss with the tomato meat sauce.</para></listitem><listitem><para>Plate and top each serving with freshly grated Parmesan cheese and a drizzle of oil.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://17\" xlink:show=\"none\">Sheri Wetherell</link>via<link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis><emphasis>Photo credit:<emphasis><link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></emphasis></emphasis></para></section>\n','ezrichtext',2834,'eng-GB',2,0,'',4);
