INSERT INTO `ezcontentobject_attribute` VALUES (0,773,230,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>10 eggs</para></listitem><listitem><para>1/2 cup almond milk (or regular)</para></listitem><listitem><para>1/2 cup diced prosciutto (I bought mine precut)</para></listitem><listitem><para>1 pint Divine Flavor fresh grape tomatoes</para></listitem><listitem><para>1 cup tightly packed chopped kale</para></listitem><listitem><para>salt and pepper to taste</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Preheat oven to 325.</para></listitem><listitem><para>Heat a skillet over medium-high heat and add prosciutto. Saute for a few minutes stirring often.</para></listitem><listitem><para>Add the kale and tomatoes and cook for another 3-4 minutes or until kale starts to wilt. Remove from heat.</para></listitem><listitem><para>Spray the bottom of one muffin tin with cooking spray.</para></listitem><listitem><para>Add 2 tbsp or so of the filling into each muffin well.</para></listitem><listitem><para>In a medium bowl add 10 eggs and 1/2 cup milk. Add salt and pepper and whisk really well until all eggs are mixed.</para></listitem><listitem><para>Carefully pour into each muffin well. Fill about 3/4 full.</para></listitem><listitem><para>Bake for 25 minutes or until quiche cups are set. Remove from oven and serve immediately. If using for meal prep, let cool and store in plastic container in fridge for up to three days.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://25\" xlink:show=\"none\">Karen Kelly</link>via<link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis><emphasis>Photo credit:<emphasis><link xlink:href=\"ezurl://18\" xlink:show=\"none\">Foodista</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></emphasis></emphasis></para></section>\n','ezrichtext',2874,'eng-GB',2,0,'',4);
