INSERT INTO `ezcontentobject_attribute` VALUES (0,771,231,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>Since these pancakes have half the number of carbs, they will leave you feeling satisfied but not stuffed and are a great choice for diabetics.</para></section>\n','ezrichtext',2892,'eng-GB',2,0,'',2);
