INSERT INTO `ezcontentobject_attribute` VALUES (0,771,232,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>This recipe, for which we highly recommend usinghomemade bread,combines kale, spinach, avocado, and goat cheese together in a delicious grilled cheese.</para></section>\n','ezrichtext',2912,'eng-GB',2,0,'',5);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,232,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',2913,'eng-GB',2,0,'',5);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,232,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>2 slices<link xlink:href=\"ezurl://27\" xlink:show=\"none\">bread</link></para></listitem><listitem><para>2 tablespoons<link xlink:href=\"ezurl://28\" xlink:show=\"none\">kale pesto</link></para></listitem><listitem><para>2 slices mozzarella cheese</para></listitem><listitem><para> cup baby spinach</para></listitem><listitem><para> avocado, sliced</para></listitem><listitem><para>3 tablespoons crumbled goat cheese</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Preheat a cast iron skillet over medium heat.</para></listitem><listitem><para>Spread kale pesto on each slice of bread.</para></listitem><listitem><para>Place a slice of mozzarella (or your other favourite cheese) on each slice of bread.</para></listitem><listitem><para>Place baby spinach on top of one slice of bread.</para></listitem><listitem><para>Slice avocado and place on other piece of bread. Top with several clumps of crumbled goat cheese.</para></listitem><listitem><para>Quickly and carefully put the sandwich together and grill on a cast iron skillet over medium heat until bread is toasted and cheese has melted, about 3 minutes per side. It may be helpful to cover the skillet with the lid of a large pot to help the cheese melt.</para></listitem><listitem><para>Serve immediately.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis><emphasis>Photo credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></emphasis></para></section>\n','ezrichtext',2914,'eng-GB',2,0,'',5);
