INSERT INTO `ezcontentobject_attribute` VALUES (0,771,233,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>Store bought hummus is bland and expensive. This simple, no-fuss, and much healthier recipe makes a delicious hummus for a fraction of the price.</para></section>\n','ezrichtext',2932,'eng-GB',2,0,'',4);
