INSERT INTO `ezcontentobject_attribute` VALUES (0,773,233,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>15oz /15 ounces organic garbanzo beans (chickpeas), drained</para></listitem><listitem><para>2 tablespoons water or liquid from canned garbanzo beans</para></listitem><listitem><para>40g /2 tablespoons tahini (sesame paste)</para></listitem><listitem><para>2 tablespoons lemon juice</para></listitem><listitem><para>2 tablespoons canola oil</para></listitem><listitem><para>1 tablespoon olive oil</para></listitem><listitem><para>1 large clove garlic, minced</para></listitem><listitem><para>2 teaspoons coarse sea salt, or to taste</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Place all ingredients in a food processor fitted with a steel blade. Cover and process until pureed.</para></listitem><listitem><para>Serve with pita chips, warm pitas, or veggies. Store remaining hummus in an airtight container in the fridge for up to 10 days.</para></listitem></orderedlist><para><literallayout class=\"normal\"><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis>\n<emphasis>Photo credit:<link xlink:href=\"ezurl://343\" xlink:show=\"none\">Marco Verch</link>/<link xlink:href=\"ezurl://344\" xlink:show=\"none\">CC BY</link></emphasis></literallayout></para></section>\n','ezrichtext',2934,'eng-GB',2,0,'',4);
