INSERT INTO `ezcontentobject_attribute` VALUES (0,771,240,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>An amazing French toast recipe.If you don\'t have almond extract, substitute vanilla extract for an equally fantastic sandwich.</para></section>\n','ezrichtext',3100,'eng-GB',2,0,'',4);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,240,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',3101,'eng-GB',2,0,'',4);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,240,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>1 large egg</para></listitem><listitem><para>2 tablespoons unsalted butter, melted, plus extra for frying</para></listitem><listitem><para> cup milk</para></listitem><listitem><para>2 teaspoons almond extract</para></listitem><listitem><para> cup unbleached all-purpose flour</para></listitem><listitem><para>2 tablespoons sugar</para></listitem><listitem><para> teaspoon table salt</para></listitem><listitem><para>6 to 8 slices day-oldsandwich bread</para></listitem><listitem><para>1 tablespoon of butter for each slice, 6-8 slices total</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Heat 10- to 12-inch skillet (preferably cast-iron) over medium heat for 5 minutes.</para></listitem><listitem><para>Meanwhile, beat egg lightly in shallow pan or pie plate.</para></listitem><listitem><para>Whisk butter into egg.</para></listitem><listitem><para>Whisk milk and almond extract into mixture.</para></listitem><listitem><para>Finally, whisk flour, sugar, and salt. Continue to whisk until smooth.</para></listitem><listitem><para>Soak bread without over-saturating, about 40 seconds per side for challah or 30 seconds per side for sandwich bread. Pick up bread and allow excess batter to drip off; repeat with remaining slices.</para></listitem><listitem><para>Swirl 1 tablespoon butter in hot skillet. Transfer prepared bread to skillet; cook until golden brown, about 1 minute 45 seconds on first side and 1 minute on the second.</para></listitem><listitem><para>Serve immediately. Continue, adding 1 tablespoon butter to skillet for each new batch.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis>Photo credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para></section>\n','ezrichtext',3102,'eng-GB',2,0,'',4);
