INSERT INTO `ezcontentobject_attribute` VALUES (0,771,241,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>Add a colorful and tasty zing to your sweet potatoes by adding cranberries and pineapple. They compliment and accent the natural sweetness while also adding a tangy dimension.</para></section>\n','ezrichtext',3121,'eng-GB',2,0,'',2);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,241,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',3122,'eng-GB',2,0,'',2);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,241,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>4 to 5 large sweet potatoes or yams</para></listitem><listitem><para>4 tablespoons butter</para></listitem><listitem><para> cup milk</para></listitem><listitem><para> teaspoon salt, or to taste</para></listitem><listitem><para>1/8 teaspoon pepper, or to taste</para></listitem><listitem><para>1 teaspoon nutmeg, or to taste</para></listitem><listitem><para> teaspoon cinnamon, or to taste</para></listitem><listitem><para>15 ounces whole berry cranberry sauce</para></listitem><listitem><para>1 small can of crushed pineapples, undrained</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Peel the sweet potatoes and chop into 1 to 2 inch thick slices. Place in a large stock pot and fill with water until potatoes are completely submerged and are able to float with about  inch of room.</para></listitem><listitem><para>Boil over high heat until potatoes are tender, approximately 30 minutes.</para></listitem><listitem><para>Once tender, drain potatoes and place in a large mixing bowl. Add butter or margarine, milk, and season with salt and pepper. Whip the potatoes for one to two minutes until butter is well mixed.</para></listitem><listitem><para>Add nutmeg and cinnamon and whip potatoes until smooth and creamy.</para></listitem><listitem><para>Stir in cranberries and crushed pineapple.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis>Photo credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para></section>\n','ezrichtext',3123,'eng-GB',2,0,'',2);
