INSERT INTO `ezcontentobject_attribute` VALUES (0,771,242,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>The key to making great guacamole is to use fresh, ripe avocados.When you cut the avocado open, it should be a vibrant green that yearns to listen to salsa music and dance with you.</para></section>\n','ezrichtext',3142,'eng-GB',2,0,'',3);
