INSERT INTO `ezcontentobject_attribute` VALUES (0,771,243,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>This recipe is an interesting twist on normalgreek salad. Quinoa is a \'super grain\' that\'s packed with vitamins and nutrients. It\'s easy to prepare and goes very well with the vegetables used here.</para></section>\n','ezrichtext',3163,'eng-GB',2,0,'',2);
INSERT INTO `ezcontentobject_attribute` VALUES (0,772,243,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"/>\n','ezrichtext',3164,'eng-GB',2,0,'',2);
INSERT INTO `ezcontentobject_attribute` VALUES (0,773,243,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><para>For quinoa:</para><itemizedlist><listitem><para>1 cup quinoa</para></listitem><listitem><para>2 cups water</para></listitem><listitem><para> teaspoon salt</para></listitem></itemizedlist><para>For the salad:</para><itemizedlist><listitem><para>1 cup grape tomatoes, halved</para></listitem><listitem><para>1/8 teaspoon salt</para></listitem><listitem><para>1 medium cucumber</para></listitem><listitem><para>1/8 teaspoon salt</para></listitem><listitem><para> small red onion</para></listitem><listitem><para> cup pitted kalamata olives, or to taste</para></listitem><listitem><para>4 ounces (about  cup) feta cheese</para></listitem></itemizedlist><para>For vinaigrette:</para><itemizedlist><listitem><para>2 teaspoons red wine vinegar</para></listitem><listitem><para>2 teaspoons fresh oregano, chopped OR 1 teaspoon dried oregano</para></listitem><listitem><para>1 teaspoon lemon juice</para></listitem><listitem><para> teaspoon salt, or to taste</para></listitem><listitem><para> teaspoon fresh ground black pepper, or to taste</para></listitem><listitem><para>5 tablespoons extra-virgin olive oil</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><para>Quinoa</para><orderedlist><listitem><para>Using a fine mesh strainer, rinse quinoa under cold water.</para></listitem><listitem><para>Bring quinoa, water, and salt to a boil in a medium saucepan over medium heat. Boil for 5 minutes and then reduce heat to a simmer. Cook until quinoa is soft, about 15 minutes. Remove from heat and fluff with a fork. Allow quinoa to cool to room temperature.</para></listitem><listitem><para>Transfer quinoa to a large bowl.</para></listitem></orderedlist><para>Salad</para><orderedlist><listitem><para>Cut grape tomatoes in half and season lightly with salt. Place in a colander over the sink to let excess water drain.</para></listitem><listitem><para>Peel cucumber, cut into quarters lengthwise, thickly slice, and add to colander with tomatoes. Season with additional salt and toss to combine. Let rest.</para></listitem><listitem><para>Dice onion and place in a large bowl with quinoa. Add olives.</para></listitem><listitem><para>Gently press excess water from tomatoes and cucumbers and add to bowl.</para></listitem><listitem><para>Crumble feta cheese over vegetables.</para></listitem></orderedlist><para>Vinaigrette</para><orderedlist><listitem><para>In a small mixing bowl or measuring cup, whisk together vinegar, lemon juice, and spices.</para></listitem><listitem><para>Add the olive oil to the vinegar mixture in a slow, steady stream while whisking. Whisk vigorously to combine.</para></listitem><listitem><para>Add the vinaigrette to the salad and toss to combine. Taste and add additional salt or pepper as desired. Serve immediately.</para></listitem></orderedlist><para><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para><para><emphasis>Photo credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis></para></section>\n','ezrichtext',3165,'eng-GB',2,0,'',2);
