INSERT INTO `ezcontentobject_attribute` VALUES (0,771,244,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>This thick, hearty soup is delicious andwell worth the extra effort to bake your own loaf of bread to go with it.</para></section>\n','ezrichtext',3184,'eng-GB',2,0,'',4);
