INSERT INTO `ezcontentobject_attribute` VALUES (0,773,244,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>3 large carrots</para></listitem><listitem><para>2 large parsnips</para></listitem><listitem><para>2 small onions</para></listitem><listitem><para>2 medium russet potatoes, peeled</para></listitem><listitem><para>2 tablespoons vegetable oil</para></listitem><listitem><para>reserved carrots, parsnips, and onions</para></listitem><listitem><para>6 cloves garlic, minced</para></listitem><listitem><para>8 cups low-sodium chicken broth</para></listitem><listitem><para>2 teaspoons minced fresh thyme leaves</para></listitem><listitem><para>1 bay leaf</para></listitem><listitem><para>pinch rosemary</para></listitem><listitem><para>reserved potatoes</para></listitem><listitem><para>2 slices (hearty) white bread, lightly toasted</para></listitem><listitem><para>2 cups curly spinach, stemmed and chopped</para></listitem><listitem><para>1 (14.5-ounce) can cannellini beans, drained and rinsed</para></listitem><listitem><para>1 (10-ounce) package frozen baby lima beans or frozen peas</para></listitem><listitem><para>1 tablespoon balsamic vinegar</para></listitem><listitem><para>1 teaspoon salt, or to taste</para></listitem><listitem><para> teaspoon pepper, or to taste</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Peel and slice carrots, parsnips, and onions into 1/2-inch pieces. Reserve in a bowl.</para></listitem><listitem><para>Dice potatoes and reserve in a small, separate bowl.</para></listitem><listitem><para>Heat oil in a large stockpot over medium-high heat until shimmering, about 3 minutes.</para></listitem><listitem><para>Add carrots, parsnips, and onions and cook until softened and just starting to brown, about 5 minutes.</para></listitem><listitem><para>Add garlic and cook until fragrant, about 1 minute.</para></listitem><listitem><para>Add chicken broth, thyme, bay leaf, rosemary, and reserved potatoes. Bring to a boil.</para></listitem><listitem><para>Reduce heat to low, cover, and simmer until vegetables are soft, about 15 minutes.</para></listitem><listitem><para>Remove bay leaf and discard.</para></listitem><listitem><para>Using a ladle or strainer, remove 3 cups of solids from pot and transfer to a food processor. Add 1 cup of broth from pot.</para></listitem><listitem><para>Tear bread in small pieces and add to food processor. Puree with vegetables and stock until smooth.</para></listitem><listitem><para>Pour puree back into pot.</para></listitem><listitem><para>Add spinach, cannellini beans, and lima beans or peas to pot and cook over medium heat until heated through, about 10 minutes.</para></listitem><listitem><para>Remove from heat and add balsamic vinegar, salt, and pepper.</para></listitem></orderedlist><para><literallayout class=\"normal\"><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis>\n<emphasis>Photo credit:<link xlink:href=\"ezurl://348\" xlink:show=\"none\">Nathalie Jolie</link></emphasis></literallayout></para></section>\n','ezrichtext',3186,'eng-GB',2,0,'',4);
