INSERT INTO `ezcontentobject_attribute` VALUES (0,771,246,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><para>These muffins are moist and have a rich, nutty flavor. We recommend this recipe for anyone with Type 1 diabetes. Since they are made with agave, peanut butter, and almond flour, their glycemic index is much lower than most breakfast foods.</para></section>\n','ezrichtext',3226,'eng-GB',2,0,'',3);
