INSERT INTO `ezcontentobject_attribute` VALUES (0,773,246,0,1045487555,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<section xmlns=\"http://docbook.org/ns/docbook\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ezxhtml=\"http://ez.no/xmlns/ezpublish/docbook/xhtml\" xmlns:ezcustom=\"http://ez.no/xmlns/ezpublish/docbook/custom\" version=\"5.0-variant ezpublish-1.0\"><title ezxhtml:level=\"3\">INGREDIENTS</title><itemizedlist><listitem><para>cooking spray</para></listitem><listitem><para>114g / cup Greek yogurt</para></listitem><listitem><para>64g / cup peanut butter</para></listitem><listitem><para>32g /3 tablespoons sugar</para></listitem><listitem><para>42g /2 tablespoons Agave syrup</para></listitem><listitem><para>1 egg</para></listitem><listitem><para>2 mashed bananas</para></listitem><listitem><para>1 teaspoonvanilla extract</para></listitem><listitem><para>125g /1  cup almond flour</para></listitem><listitem><para>1 teaspoon baking powder</para></listitem><listitem><para> teaspoon baking soda</para></listitem><listitem><para> teaspoon salt</para></listitem></itemizedlist><title ezxhtml:level=\"3\">PREPARATION</title><orderedlist><listitem><para>Preheat oven to 375F. Lightly grease a muffin tin with cooking spray.</para></listitem><listitem><para>In a large bowl, beat yogurt, peanut butter, sugar, and Agave syrup with an electric hand mixer until well combined.</para></listitem><listitem><para>Add egg and beat well with electric hand mixer.</para></listitem><listitem><para>Add mashed banana and vanilla extract and beat until combined.</para></listitem><listitem><para>Add almond flour, baking powder, baking soda, and salt to bowl. Beat on low speed until well combined.</para></listitem><listitem><para>Using a  cup measure, pour into muffin pan. Bake at 375F until muffins are golden brown and a toothpick inserted into the center comes out clean, about 18 to 20 minutes. If you have an instant read thermometer, the interiors should reach 200F.</para></listitem><listitem><para>Remove from oven and let cool in pan for 5 minutes. Gently remove from pan and let cool completely on wire rack.Muffins will keep in an airtight container for 5 days.</para></listitem></orderedlist><para><literallayout class=\"normal\"><emphasis>Recipe credit:<link xlink:href=\"ezurl://26\" xlink:show=\"none\">Make Better Food</link>/<link xlink:href=\"ezurl://19\" xlink:show=\"none\">CC BY</link></emphasis>\n<emphasis>Photo credit:<link xlink:href=\"ezurl://345\" xlink:show=\"none\">Mink Mingle</link></emphasis></literallayout></para></section>\n','ezrichtext',3228,'eng-GB',2,0,'',3);
