INSERT INTO `ezcontentobject_attribute` VALUES (0,351,370,0,NULL,'<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<ezimage serial_number=\"1\" is_valid=\"1\" filename=\"effective-training-techniques-isolation-vs-compound-movements.jpg\" suffix=\"jpg\" basename=\"effective-training-techniques-isolation-vs-compound-movements\" dirpath=\"var/site/storage/images/fitness/effective-training-techniques-isolation-vs-compound-movements/3712-10-eng-GB\" url=\"var/site/storage/images/fitness/effective-training-techniques-isolation-vs-compound-movements/3712-10-eng-GB/effective-training-techniques-isolation-vs-compound-movements.jpg\" original_filename=\"sam-sabourin-481686-unsplash.jpg\" mime_type=\"image/jpeg\" width=\"4048\" height=\"3036\" alternative_text=\"\" alias_key=\"1293033771\" timestamp=\"1535053307\"><original attribute_id=\"3712\" attribute_version=\"10\" attribute_language=\"eng-GB\"/><information Height=\"3036\" Width=\"4048\" IsColor=\"1\"/></ezimage>\n','ezimage',4322,'eng-GB',2,0,'',12);
